          wt.Open("template.doc");

          //--- Set the repeat block defined by the bookmark "Block"
          //--- The data source is the 2-D rectangular ValuesArr array
          wt.SetRepeatBlock(ValuesArr, NamesArr, "Block");
          wt.Process();
          wt.Save("out.doc");

